exports.mongoDB = function(){ 
	var databaseUrl = "db"; 
	var collections = ["users"]
	var db = require("mongojs").connect(databaseUrl, collections);
	return db;
};

exports.registerUser = function(params){
	db.users.save({
		username: params.username,
		email: params.email, 
		password: params.password, 
		firstName: params.firstName,
		lastName: params.lastName,
		dob: params.dob
	}, 
	function(err, saved) {
		if( err || !saved ) console.log("User not saved");
		else console.log("User saved");
	});
}

//.. example params{email: "myemail@gmail.com", password: "Newpassword"}
exports.updateRecord = function(params){
	db.users.update({email: params.email},{$set: {password: params.password}}, 
	function(err, updated) {
		if( err || !updated ) console.log("User not updated");
		else console.log("User updated");
	});
}